mapper.select:

select

  distinct


from tb_activity




  order by ${orderByClause}


	limit #{start},#{limit}



    select * from
    (
    select DISTINCT a.*
    from tb_activity a left join tb_activity_college ac on a.activity_id = ac.activity_id
    where 1=1 and a.is_del=0

    	and a.create_time >= #{startTime}


    	and a.create_time <=#{endTime}


    	and activity_name like #{name} or activity_show_name like #{name}


    	and a.activity_type = #{type}


    	and a.activity_type in (0,1,5) 


    	and a.platform_type = #{platformType}



       and a.program_type = #{programType}


        and a.batch_ids = #{batchIds},


       and a.value = #{value},



    	and a.is_open = #{status}


		and ac.college_id in ${collegeIds}


       and a.display_type = #{displayType}


	order by a.first_index desc, a.create_time desc
    ) a     

    	limit #{start},#{limit}


    select count(*) from
    (
    select DISTINCT a.*
    from tb_activity a left join tb_activity_college ac on a.activity_id = ac.activity_id
    where 1=1  and a.is_del=0

    	and a.create_time >= #{startTime}


    	and a.create_time <=#{endTime}


    	and activity_name like #{name} or activity_show_name like #{name}


    	and a.activity_type = #{type}


    	and a.activity_type in (0,1,5) 


    	and a.platform_type = #{platformType}


       and a.program_type = #{programType}


        and a.batch_ids = #{batchIds},


       and a.value = #{value},


    	and a.is_open = #{status}


		and ac.college_id in ${collegeIds}


       and a.display_type = #{displayType}

    ) a



    select * from
    (
    select DISTINCT a.*
    from tb_activity a left join tb_activity_college ac on a.activity_id = ac.activity_id
    where 1=1 and a.is_del=0

    	and a.create_time >= #{startTime}


    	and a.create_time <=#{endTime}


    	and activity_name like #{name} or activity_show_name like #{name}


    	and a.activity_type = #{type}


    	and a.activity_type in (2,3,4,6,7) 


    	and a.platform_type = #{platformType}



       and a.program_type = #{programType}


        and a.batch_ids = #{batchIds},


       and a.value = #{value},



    	and a.is_open = #{status}


		and ac.college_id in ${collegeIds}


	order by a.create_time desc
    ) a     

    	limit #{start},#{limit}


    select count(*) from
    (
    select DISTINCT a.*
    from tb_activity a left join tb_activity_college ac on a.activity_id = ac.activity_id
    where 1=1  and a.is_del=0

    	and a.create_time >= #{startTime}


    	and a.create_time <=#{endTime}


    	and activity_name like #{name} or activity_show_name like #{name}


    	and a.activity_type = #{type}


    	and a.activity_type in (2,3,4,6,7) 


    	and a.platform_type = #{platformType}


       and a.program_type = #{programType}


        and a.batch_ids = #{batchIds},


       and a.value = #{value},


    	and a.is_open = #{status}


		and ac.college_id in ${collegeIds}

    ) a
